import{_ as t}from"./chunks/screenshot.BMAzETFi.js";import{_ as a,o as r,c as s,ag as i}from"./chunks/framework.gFULg22W.js";const m=JSON.parse('{"title":"Usage","description":"","frontmatter":{},"headers":[],"relativePath":"guide/usage.md","filePath":"guide/usage.md"}'),n={name:"guide/usage.md"};function o(l,e,d,u,h,c){return r(),s("div",null,[...e[0]||(e[0]=[i('<h1 id="usage" tabindex="-1">Usage <a class="header-anchor" href="#usage" aria-label="Permalink to &quot;Usage&quot;">​</a></h1><p>This guide covers the basics of using Apiary&#39;s interface and features.</p><h2 id="interface-overview" tabindex="-1">Interface Overview <a class="header-anchor" href="#interface-overview" aria-label="Permalink to &quot;Interface Overview&quot;">​</a></h2><p>When you first open Apiary, you&#39;ll see the main interface divided into several areas:</p><p><img src="'+t+`" alt=""></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>┌────────────────┬────────────────────────────────────────────┐</span></span>
<span class="line"><span>│                │                                            │</span></span>
<span class="line"><span>│                │                                            │</span></span>
<span class="line"><span>│                │                                            │</span></span>
<span class="line"><span>│  Sidebar       │              Main Workspace                │</span></span>
<span class="line"><span>│  (Collections) │              (Tabs &amp; Panels)               │</span></span>
<span class="line"><span>│                │                                            │</span></span>
<span class="line"><span>│                │                                            │</span></span>
<span class="line"><span>│                │                                            │</span></span>
<span class="line"><span>└────────────────┴────────────────────────────────────────────┘</span></span></code></pre></div><h3 id="sidebar" tabindex="-1">Sidebar <a class="header-anchor" href="#sidebar" aria-label="Permalink to &quot;Sidebar&quot;">​</a></h3><p>The left sidebar shows your collections tree:</p><ul><li><strong>Directories</strong>: Folders to organize requests</li><li><strong>Requests</strong>: Individual queries (HTTP, SQL, gRPC, etc.)</li><li><strong>Datasources</strong>: External collections (OpenAPI, databases, file systems)</li><li><strong>Recently Opened</strong>: Quick access to recent requests</li></ul><h3 id="main-workspace" tabindex="-1">Main Workspace <a class="header-anchor" href="#main-workspace" aria-label="Permalink to &quot;Main Workspace&quot;">​</a></h3><p>The main area uses a tabbed interface powered by GoldenLayout. You can:</p><ul><li>Open multiple requests side-by-side</li><li>Split panels horizontally or vertically</li><li>Drag and drop tabs to reorganize</li><li>Close tabs with the × button</li></ul><h3 id="menu-bar" tabindex="-1">Menu Bar <a class="header-anchor" href="#menu-bar" aria-label="Permalink to &quot;Menu Bar&quot;">​</a></h3><p>The top menu provides access to:</p><ul><li><strong>File</strong>: New request, save, import/export</li><li><strong>Edit</strong>: Undo/redo, copy/paste</li><li><strong>View</strong>: Toggle sidebar, theme, zoom</li><li><strong>Request</strong>: Perform request, cancel, settings</li><li><strong>Help</strong>: Documentation, about</li></ul><h2 id="creating-your-first-request" tabindex="-1">Creating Your First Request <a class="header-anchor" href="#creating-your-first-request" aria-label="Permalink to &quot;Creating Your First Request&quot;">​</a></h2><ol><li>Click <strong>File → New Request</strong> or press <code>Ctrl+N</code></li><li>Select a request type (HTTP, SQL, gRPC, etc.)</li><li>The new request tab will open in the main workspace</li><li>Configure the request (URL, query, parameters, etc.)</li><li>Click the <strong>Perform</strong> button (or press <code>Ctrl+Enter</code>) to execute</li><li>View the response in the right panel</li></ol><h2 id="request-types" tabindex="-1">Request Types <a class="header-anchor" href="#request-types" aria-label="Permalink to &quot;Request Types&quot;">​</a></h2><p>Each request type has its own configuration interface:</p><h3 id="http-requests" tabindex="-1">HTTP Requests <a class="header-anchor" href="#http-requests" aria-label="Permalink to &quot;HTTP Requests&quot;">​</a></h3><ul><li><strong>Method</strong>: GET, POST, PUT, DELETE, etc.</li><li><strong>URL</strong>: The endpoint to call</li><li><strong>Headers</strong>: Key-value pairs for HTTP headers</li><li><strong>Body</strong>: Request body with syntax highlighting</li><li><strong>Parameters</strong>: Query parameters and form data</li></ul><h3 id="sql-queries" tabindex="-1">SQL Queries <a class="header-anchor" href="#sql-queries" aria-label="Permalink to &quot;SQL Queries&quot;">​</a></h3><ul><li><strong>Database Type</strong>: SQLite, PostgreSQL, ClickHouse, etc.</li><li><strong>Connection String</strong>: DSN for the database</li><li><strong>Query</strong>: SQL editor with autocomplete</li><li><strong>Parameters</strong>: Query parameters for prepared statements</li></ul><h3 id="grpc-calls" tabindex="-1">gRPC Calls <a class="header-anchor" href="#grpc-calls" aria-label="Permalink to &quot;gRPC Calls&quot;">​</a></h3><ul><li><strong>Server Address</strong>: gRPC server endpoint</li><li><strong>Service/Method</strong>: Select from reflection or proto files</li><li><strong>Request Message</strong>: JSON format for the request</li><li><strong>Metadata</strong>: Key-value pairs for gRPC metadata</li></ul><h3 id="redis-commands" tabindex="-1">Redis Commands <a class="header-anchor" href="#redis-commands" aria-label="Permalink to &quot;Redis Commands&quot;">​</a></h3><ul><li><strong>Server Address</strong>: Redis server endpoint</li><li><strong>Command</strong>: Redis command (GET, SET, etc.)</li><li><strong>Arguments</strong>: Command arguments</li><li><strong>Database Index</strong>: Select Redis database</li></ul><h3 id="jq-transformations" tabindex="-1">JQ Transformations <a class="header-anchor" href="#jq-transformations" aria-label="Permalink to &quot;JQ Transformations&quot;">​</a></h3><ul><li><strong>Input</strong>: JSON input (can be from another request)</li><li><strong>JQ Query</strong>: JQ filter expression</li><li><strong>Output</strong>: Transformed JSON result</li></ul><h3 id="markdown-documents" tabindex="-1">Markdown Documents <a class="header-anchor" href="#markdown-documents" aria-label="Permalink to &quot;Markdown Documents&quot;">​</a></h3><ul><li><strong>Content</strong>: Markdown editor with live preview</li><li><strong>Rendering</strong>: HTML preview with styling</li></ul><h2 id="performing-requests" tabindex="-1">Performing Requests <a class="header-anchor" href="#performing-requests" aria-label="Permalink to &quot;Performing Requests&quot;">​</a></h2><p>Once you&#39;ve configured a request:</p><ol><li>Click the <strong>Perform</strong> button (▶️) in the navigation line</li><li>The response will appear in the right panel</li><li>Response details (status code, time, size) show in the status bar</li><li>Use tabs to switch between different response views: <ul><li><strong>Body</strong>: The main response content</li><li><strong>Headers/Metadata</strong>: Response headers or metadata</li><li><strong>Info</strong>: Performance metrics and timing</li></ul></li></ol><h2 id="working-with-responses" tabindex="-1">Working with Responses <a class="header-anchor" href="#working-with-responses" aria-label="Permalink to &quot;Working with Responses&quot;">​</a></h2><h3 id="json-responses" tabindex="-1">JSON Responses <a class="header-anchor" href="#json-responses" aria-label="Permalink to &quot;JSON Responses&quot;">​</a></h3><ul><li>Syntax highlighting with collapsible sections</li><li>JQ query bar to filter and transform</li><li>Copy button for quick copying</li></ul><h3 id="table-responses-sql-results" tabindex="-1">Table Responses (SQL results) <a class="header-anchor" href="#table-responses-sql-results" aria-label="Permalink to &quot;Table Responses (SQL results)&quot;">​</a></h3><ul><li>Sort by clicking column headers</li><li>Filter using the filter bar</li><li>Export to CSV or JSON</li></ul><h3 id="binary-responses-images-files" tabindex="-1">Binary Responses (images, files) <a class="header-anchor" href="#binary-responses-images-files" aria-label="Permalink to &quot;Binary Responses (images, files)&quot;">​</a></h3><ul><li>Image viewer for images</li><li>Hex viewer for binary data</li><li>File type detection</li></ul><h3 id="html-responses" tabindex="-1">HTML Responses <a class="header-anchor" href="#html-responses" aria-label="Permalink to &quot;HTML Responses&quot;">​</a></h3><ul><li>Built-in browser rendering</li><li>Toggle between source and rendered views</li></ul><h2 id="organizing-requests" tabindex="-1">Organizing Requests <a class="header-anchor" href="#organizing-requests" aria-label="Permalink to &quot;Organizing Requests&quot;">​</a></h2><h3 id="creating-directories" tabindex="-1">Creating Directories <a class="header-anchor" href="#creating-directories" aria-label="Permalink to &quot;Creating Directories&quot;">​</a></h3><p>Right-click in the sidebar and select <strong>New Directory</strong> to create folders for organizing requests.</p><h3 id="moving-requests" tabindex="-1">Moving Requests <a class="header-anchor" href="#moving-requests" aria-label="Permalink to &quot;Moving Requests&quot;">​</a></h3><p>Drag and drop requests between directories in the sidebar.</p><h3 id="renaming-and-deleting" tabindex="-1">Renaming and Deleting <a class="header-anchor" href="#renaming-and-deleting" aria-label="Permalink to &quot;Renaming and Deleting&quot;">​</a></h3><p>Right-click a request or directory and select <strong>Rename</strong> or <strong>Delete</strong>.</p><h2 id="keyboard-shortcuts" tabindex="-1">Keyboard Shortcuts <a class="header-anchor" href="#keyboard-shortcuts" aria-label="Permalink to &quot;Keyboard Shortcuts&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Shortcut</th><th>Action</th></tr></thead><tbody><tr><td><code>Ctrl+N</code></td><td>New request</td></tr><tr><td><code>Ctrl+O</code></td><td>Open request</td></tr><tr><td><code>Ctrl+S</code></td><td>Save request</td></tr><tr><td><code>Ctrl+Enter</code></td><td>Perform request</td></tr><tr><td><code>Ctrl+Shift+P</code></td><td>Command palette</td></tr><tr><td><code>Ctrl+Tab</code></td><td>Next tab</td></tr><tr><td><code>Ctrl+Shift+Tab</code></td><td>Previous tab</td></tr><tr><td><code>Ctrl+W</code></td><td>Close tab</td></tr><tr><td><code>Ctrl+</code></td><td>Split panel</td></tr></tbody></table><h2 id="command-palette" tabindex="-1">Command Palette <a class="header-anchor" href="#command-palette" aria-label="Permalink to &quot;Command Palette&quot;">​</a></h2><p>Press <code>Ctrl+Shift+P</code> to open the command palette, which lets you quickly access all features without using the mouse.</p><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><p>Explore the specific request type guides to learn more about each feature:</p><ul><li><a href="/apiary/guide/http.html">HTTP Requests</a></li><li><a href="/apiary/guide/sql.html">SQL Queries</a></li><li><a href="/apiary/guide/grpc.html">gRPC Calls</a></li><li><a href="/apiary/guide/redis.html">Redis Commands</a></li><li><a href="/apiary/guide/jq.html">JQ Transformations</a></li><li><a href="/apiary/guide/markdown.html">Markdown Documents</a></li><li><a href="/apiary/guide/sqlsource.html">SQLSource</a></li><li><a href="/apiary/guide/httpsource.html">HTTPSource</a></li></ul>`,57)])])}const b=a(n,[["render",o]]);export{m as __pageData,b as default};
